import java.util.Scanner;
public class rev {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		String s5=sc.next();
      StringBuffer s=new StringBuffer(s5);
	s.reverse();
	String  s1=s.toString();
	s1=deleteAll(s1,"e");
	s1=deleteAll(s1,"a");
	s1=deleteAll(s1,"i");
	s1=deleteAll(s1,"o");
	s1=deleteAll(s1,"u");
	System.out.println(s1);
	}

	private static String deleteAll(String s1, String charToRemove) {
		// TODO Auto-generated method stub
		return s1.replaceAll(charToRemove,"");
	}

	

}
